settings.custom_liquid:
{% if product.handle == 'autocue-coil-spring-buffer-sizes-a-b-c-d-pure-polyurethane-pu_new-model' %}
  <!-- Display "Compatibility Chart" Link -->
  <div class="product-size-chart">
    <p>
      <strong>
        <a href="#" class="open-compatibility-chart" style="color: black; font-family: Arial, sans-serif; text-shadow: 0 0 1px black;">
          Compatibility Chart
        </a>
      </strong>
    </p>
  </div>

  <!-- Modal Popup for Compatibility Chart -->
  <div id="compatibilityChartModal" class="modal">
    <div class="modal-content">
      <div class="modal-body">
        <h2 style="font-family: Arial, sans-serif; margin-bottom: 10px; color: black;">Compatibility Chart</h2>
        <div class="table-container">
          <table class="compatibility-table">
            <thead>
              <tr>
                <th>Size</th>
                <th>Model</th>
                <th>Diameter (mm)</th>
                <th>Height (mm)</th>
                <th>Material</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>A</td>
                <td>Model X</td>
                <td>50</td>
                <td>100</td>
                <td>PU</td>
              </tr>
              <tr>
                <td>B</td>
                <td>Model Y</td>
                <td>60</td>
                <td>110</td>
                <td>PU</td>
              </tr>
              <tr>
                <td>C</td>
                <td>Model Z</td>
                <td>70</td>
                <td>120</td>
                <td>PU</td>
              </tr>
              <!-- Add more rows as needed -->
            </tbody>
          </table>
        </div>
      </div>
    </div>
  </div>

  <style>
    /* Modal Styling */
    .modal {
      display: none;
      position: fixed;
      z-index: 1000;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      background-color: rgba(0, 0, 0, 0.4); /* Semi-transparent background */
      overflow: hidden; /* Prevent background scrolling */
    }

    .modal-content {
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      width: 80%; /* Adjust modal width for desktop */
      max-width: 600px; /* Max width for larger screens */
      max-height: 80%; /* Limit height for scrolling */
      background-color: white;
      border-radius: 5px;
      box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
      overflow-y: auto; /* Allow scrolling within modal */
      padding: 20px;
      font-family: Arial, sans-serif;
    }

    /* Table Styling */
    .table-container {
      overflow-x: auto; /* Horizontal scrolling for tables */
    }

    .compatibility-table {
      width: 100%;
      border-collapse: collapse;
      font-family: Arial, sans-serif;
      color: black;
      text-align: left;
    }

    .compatibility-table th,
    .compatibility-table td {
      padding: 10px;
      border: 1px solid #ddd;
    }

    .compatibility-table th {
      background-color: #f4f4f4;
      font-weight: bold;
    }

    .compatibility-table tr:nth-child(even) {
      background-color: #f9f9f9;
    }

    .compatibility-table tr:hover {
      background-color: #f1f1f1;
    }

    /* Responsive Design for Mobile */
    @media only screen and (max-width: 767px) {
      .modal-content {
        width: 90%; /* Adjust modal size for smaller screens */
      }
    }
  </style>

  <!-- JavaScript to Open and Close the Modal -->
  <script>
    document.querySelector('.open-compatibility-chart').addEventListener('click', function(e) {
      e.preventDefault();
      const modal = document.getElementById('compatibilityChartModal');
      modal.style.display = 'block';
      document.body.style.overflow = 'hidden'; // Disable background scrolling
    });

    window.addEventListener('click', function(event) {
      const modal = document.getElementById('compatibilityChartModal');
      const content = modal.querySelector('.modal-content');
      if (event.target === modal && !content.contains(event.target)) {
        modal.style.display = 'none';
        document.body.style.overflow = 'auto'; // Re-enable background scrolling
      }
    });
  </script>
{% endif %}
